// SPDX-License-Identifier: MIT
pragma solidity 0.8.0;

contract Gasless {

    event Paid(address _sender, address _recipient, uint256 _amount);

    /// @dev EIP-712 variables:
    bytes32 public immutable DOMAIN_SEPARATOR;
    bytes32 public constant SIG_HASH = keccak256("DoTx(address _sender, address _recipient, uint _amount)");

    mapping(address => uint256) public balances; //in reality, may want this private

    address public admin;

    constructor() {

        admin = msg.sender;

        DOMAIN_SEPARATOR = keccak256(
            abi.encode(
                keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"),
                keccak256(bytes("Gasless")),
                keccak256(bytes("1")),
                block.chainid,
                address(this)
            )
        );
    }

    function deposit() public payable {
        balances[msg.sender] += msg.value;
    }

    function balance() external view returns (uint) {
        return address(this).balance;
    }

    function authTx(address _sender, address _recipient, uint256 _amount, uint8 _v, bytes32 _r, bytes32 _s) external {
        // Validate signature elements:
        bytes32 digest =
            keccak256(
                abi.encodePacked(
                    "\x19\x01",
                    DOMAIN_SEPARATOR,
                    keccak256(
                        abi.encode(
                            SIG_HASH,
                            _sender,
                            _amount
                        )
                    )
                )
            );
        address recovered = ecrecover(digest, _v, _r, _s);
        require(recovered == _sender, "INVALID_SIG");
        doTx(_sender, _recipient, _amount);
    }

    function doTx(address _sender, address _recipient, uint _amount) internal returns (bool success) {
        require(_amount <= balances[_sender], "Insufficient balance");
        require(address(this).balance > 23000, "Out of gas");
        (success, ) =  _recipient.call{value: _amount, gas: 23000}("");
        balances[_sender] -= _amount;
        emit Paid(_sender, _recipient, _amount);
    }
}

